ui.css:
a[href], a[href]:visited { color: blue; text-decoration: none;}

input:focus, textarea:focus, *:focus {
  border-color: #ccc !important; outline: none; box-shadow: none !important;
  -webkit-box-shadow: none !important; -moz-box-shadow: none !important
}

input:not(.toggle):not(.hidden) {
  display:inline-flex; border-radius:0.25rem; border:1px solid #ccc;
  padding: 0.25rem 0.5rem; outline: none; color:rgb(75,85,99);
}

input:disabled:not(.toggle):not(.hidden), select:disabled  { background: #f5f5f5; cursor: not-allowed; }

input:not(.toggle):not(.hidden).editing { background: rgb(254 252 232) !important; }

select {
  border-radius:0.25rem; border: 1px solid #ccc; padding:0.25rem;
  font-size:0.875rem; line-height:1.25rem; color:rgb(75,85,99);
}

.loading { animation: loading 1s infinite linear; }
@keyframes loading {
  0% { transform:  translateX(0) scaleX(0); }
  40% { transform:  translateX(0) scaleX(0.4); }
  100% { transform:  translateX(100%) scaleX(0.5); }
}

.spin { animation: spin 1s infinite linear; }
@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}


.transform { transform:translate(0, 0) rotate(0) skewX(0) skewY(0) scaleX(1) scaleY(1); }
.translate-x-0 { transform:translate(0px, 0) rotate(0) skewX(0) skewY(0) scaleX(1) scaleY(1); }
.translate-x-full { transform:translate(100%, 0) rotate(0) skewX(0) skewY(0) scaleX(1) scaleY(1); }
.-translate-x-full { transform:translate(-100%, 0) rotate(0) skewX(0) skewY(0) scaleX(1) scaleY(1); }
.ml-72 { margin-left:18rem; }
.font-bold { font-weight: 700; }
.w-full, .w-100 { width: 100%; }
.nowrap { white-space: nowrap; }
.noround { border-radius:0px; }
.grow { flex-grow: 1; }
.rounded-full { border-radius:9999px !important; }
.text-sm { font-size:0.875rem; line-height:1.25rem;  }

.status-circle { display:flex; align-items:center; gap:0.375rem; white-space:nowrap;  }
.status-circle-green { color:rgb(34,197,94); }
.status-circle-red { color:rgb(239,68,68); }

a { color: #aaf; }
table { padding:0;margin:0;border:0; border-collapse: collapse; }
td { padding: 2px 0.25rem; }

.dashboard-wrapper { position:relative; flex-grow: 1; display: flex; flex-direction: column; }

.sidebar-wrapper { position:absolute; bottom:0px; left:0px; top:0px; width:18rem; transition:all; transition-duration:300ms;  }
.page-wrapper { transition: all; transition-duration:300ms; background: rgb(241,245,249); flex-grow: 1; }
.sidebar { position:relative; height:100%; user-select:none; background: white; }
.sidebar-top { display:flex; align-items:center; gap:0.5rem; padding-left:0.75rem; padding-right:0.75rem; padding-top:0.5rem; padding-bottom:0.5rem;  }
.logo { display: inline-block; height:4rem;width:50%;cursor:pointer;align-items:center;justify-content:center; }
.logo svg { max-width: 100% !important; max-height: 100% !important; }
.brand-name { font-weight: bold; font-size: 100%; color: #677589; display:flex; flex-grow:1; justify-content:center;}
.sidebar-item-list { }
.sidebar-item { display:flex; cursor:pointer; align-items:center; gap:0.5rem; padding:0.4rem 1rem; border-bottom: 1px solid rgb(226,232,240); }
.sidebar-selected-item { background:rgb(241,245,249); outline: none; }

.page { padding: 0.75em; gap: 0.75em; min-height: 2em; display: flex; flex-direction: column; flex-grow: 1; }

.card { padding: 0.5em 0.8em; display:flex; flex-direction:column; border-radius:0.5rem; border: 1px solid rgb(226,232,240); background:white; flex: 1 1 auto; gap: 0.25rem; flex: 1 1 0; white-space: nowrap; }
.card > div:nth-child(1) { font-size: 85%; color: rgb(107,114,128); }
.card > div:nth-child(2) { font-size: 140%; font-weight: 600; color: rgb(71,85,105); }

.panel { display: flex; flex-direction: column; gap: 0.4rem; background: white; border: 1px solid rgb(226,232,240); border-radius: 0.5rem; padding: 0.5em 0.8em; }
.panel > .title { border-bottom: 1px solid rgb(226,232,240); margin: -0.5rem -0.8rem 0 -0.8rem; padding: 0.5rem 0.8em 0.3rem 0.8rem; background: #fafafa; border-radius: 0.5rem 0.5rem 0 0; }

.container-h { display: flex; gap: 0.5rem; min-height: 2em;  }
.container-v { display: flex; gap: 0.5rem; min-height: 2em; flex-direction: column; }
.wrap { flex-wrap: wrap; }
.size-equal { flex: 1 1 0; }
.size-auto { flex: 1 1 auto; }

.form-control { display:flex; align-items:center; justify-content:space-between;  }
.title { font-size: 0.875rem;  white-space: nowrap; text-overflow: ellipsis; font-weight: bold; color:rgb(71,85,105); }
.label { font-size: 0.875rem;  white-space: nowrap; text-overflow: ellipsis; color:rgb(71,85,105); }

.note { background: white; border: 1px solid rgb(226,232,240); color: rgb(108,123,144); padding: 0.5em 0.8em; border-radius: 0.5em; font-size: 90%; display: flex; flex-direction: column; gap: 0.5rem; }

@media (max-width: 640px) {
  .panel, .card { flex: 1 1 100% !important;}
}
